import {useState, useEffect} from 'react';
import './App.css';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBCardHeader,
  MDBBtn,
  MDBListGroup,
  MDBListGroupItem,
  MDBIcon,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalBody,
  MDBModalTitle,
  MDBModalFooter,
  MDBDropdown,
  MDBDropdownItem,
  MDBDropdownMenu,
  MDBDropdownToggle,
  MDBTable, 
  MDBTableHead, 
  MDBTableBody,
  MDBNavbar,
  MDBRadio
} from 'mdb-react-ui-kit';

function App() {
  type Info = { id: string, full_name: string, email: string, phone: string, mobile: string, address: string };
  type Privs = { role: string, edit_priv: string, edit: string, copy: string, view: string };
  {/* 
    HGETALL user:1
    1) "id"
    2) "@admin"
    3) "name"
    4) "Admin"
    5) "role"
    6) "2"

      HGETALL user_info:1
       1) "id"
       2) "@admin"
       3) "full_name"
       4) "Artjom"
       5) "email"
       6) "sda20036@gmail.com"
       7) "phone"
       8) "89171846394"
       9) "mobile"
      10) "-"
      11) "address"
      12) "Tatischeva"

    get num_users
    "3"

    HGETALL role_priv:1
     1) "role"
     2) "2"
     3) "edit_priv"
     4) "1"
     5) "edit"
     6) "1"
     7) "copy"
     8) "1"
     9) "view"
    10) "1"
   */}
  const [roleSettingsModal, setRoleSettingsModal] = useState(false);
  const [users, setUsers] = useState([]);
  const [userRole, setUserRole] = useState(2);
  const [userId, setUserId] = useState("@admin");
  const [userName, setUserName] = useState("Admin");
  const [userInfo, setUserInfo] = useState<Info>();
  const [userToView, setUserToView] = useState("@admin");
  const [userPrivInfo, setUserPrivInfo] = useState<Privs>();
  const toggleOpenSettings = () => setRoleSettingsModal(!roleSettingsModal);

  const handleGetAnotherUserInfo = (id: string) => {
    if(userPrivInfo?.view != "0")
    {
      // Retrieving another user info
      setUserToView(id);
      let count = 1;
      users.map((user : {id: string, name: string}) => {
        if(user.id == id) {
          fetch(`http://localhost:5000/get_user_info?id=${count}`).then(
            res => res.json()
          ).then(
            data => {
              setUserInfo(data)
              console.log(data)
            }
          );
        }
        count++;
      })
      console.log("\nyou are viewing ", id);
    }
    else {
      alert("Not enough privilliges!");
    }
  }

  const handleCopyInfo = (e : any) => {
    if(userPrivInfo?.copy == "0")
    {
      e.preventDefault();
      alert("Not enough privilliges!")
      return false;      
    }
  }

  const handleSwitchRole = (id : string) => {
    setUserId(id);
    let count = 1;
    users.map((user : {id: string, name: string, role: number}) => {
      if(user.id == id) {
        setUserRole(user.role);
        setUserName(user.name);
        console.log("found user", user)

        // Get user info
        fetch(`http://localhost:5000/get_user_info?id=${count}`).then(
          res => res.json()
        ).then(
          data => {
            setUserInfo(data)
            console.log(data)
          }
        )
        // Get user privilleges
        fetch(`http://localhost:5000/get_role_priv?role=${user.role}`).then(
          res => res.json()
        ).then(
          data => {
            setUserPrivInfo(data)
            console.log(data)
          }
        )
      }
      count++;
    })
    console.log("\nyou are now ", id);
  }

  useEffect(() => {
    fetch("http://localhost:5000/get_users").then(
      res => res.json()
    ).then(
      data => {
        setUsers(data)
        console.log(data)
      }
    );
  }, [])

  return (
    <section style={{ backgroundColor: '#0D1116', color: 'white' }}>
      {/* Modal with settings */}
      <MDBModal staticBackdrop open={roleSettingsModal} setopen={setRoleSettingsModal} tabIndex='-1'>
        <MDBModalDialog>
          <MDBModalContent style={{ backgroundColor: '#0D1116', border: '1px solid #21262D' }}>
            <MDBModalHeader style={{ backgroundColor: '#161B22', borderBottom: '1px solid #21262D'}}>
              <MDBModalTitle>Редактировать права доступа</MDBModalTitle>
              <MDBBtn className='btn-close rounded-3' color='danger' onClick={toggleOpenSettings} style={{ backgroundColor: 'red' }}></MDBBtn>
            </MDBModalHeader>

            <MDBModalBody style={{ backgroundColor: '#0D1116'}}>
              <MDBTable align='middle' className='text-light'>
                <MDBTableHead>
                  <tr>
                    <th scope='col'>User</th>
                    <th scope='col'>Edit</th>
                    <th scope='col'>Copy</th>
                    <th scope='col'>Read</th>
                    <th scope='col'></th>
                  </tr>
                </MDBTableHead>
                <MDBTableBody>
                  <tr>
                    <th scope='row'>Admin</th>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBBtn className='rounded-1 p-1' color='link' onClick={toggleOpenSettings} disabled>🗑</MDBBtn></td>
                  </tr>
                  <tr>
                    <th scope='row'>Moderator</th>
                    <td><MDBRadio id='flexRadioCheckedDisabled' disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBBtn className='rounded-1 p-1' color='link' onClick={toggleOpenSettings} disabled>🗑</MDBBtn></td>
                  </tr>
                  <tr>
                    <th scope='row'>User</th>
                    <td><MDBRadio id='flexRadioCheckedDisabled' disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' disabled/></td>
                    <td><MDBRadio id='flexRadioCheckedDisabled' defaultChecked disabled/></td>
                    <td><MDBBtn className='rounded-1 p-1' color='link' onClick={toggleOpenSettings} disabled>🗑</MDBBtn></td>
                  </tr>
                </MDBTableBody>
              </MDBTable>
            </MDBModalBody>

            <MDBModalFooter className='justify-content-center' style={{ backgroundColor: '#0D1116', borderTop: "1px solid #21262D" }}>
              <MDBBtn color='success'>Save changes</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>

      {/* Navigation bar */}
      <MDBNavbar 
      expand='lg' 
      className='p-3' 
      fixed='top' 
      style={{ backgroundColor: '#01040A', borderColor: '#1B1F26', borderBottom: '1px solid #21262D'}}>
        <MDBContainer fluid className='justify-content-start'>
          <MDBDropdown>
            <MDBDropdownToggle color='dark' className='p-1 me-3'>Юзер</MDBDropdownToggle>
            <MDBDropdownMenu style={{ backgroundColor: '#161B22'}}>
              { 
                users.map((user : {id: string, name: string}) => 
                {
                 return <MDBDropdownItem 
                 link id={user.id} onClick={() => handleSwitchRole(user.id as string)}>
                 {user.name}
                 </MDBDropdownItem>
                }) 
              }
              {/* <MDBDropdownItem link>Админ</MDBDropdownItem>
              <MDBDropdownItem link>Модератор</MDBDropdownItem>
              <MDBDropdownItem link>Пользователь</MDBDropdownItem> */}
            </MDBDropdownMenu>
          </MDBDropdown>
          <MDBBtn color='danger' className='p-1' onClick={() => toggleOpenSettings()}>Изменить права доступа</MDBBtn>
        </MDBContainer>
      </MDBNavbar>

      {/* Main content */}
      <MDBContainer className="py-5 mt-4">
        <MDBRow>
          <MDBCol lg="4">
            {/* Card for profile avatar and actions */}
            <MDBCard className="mb-4" style={{ backgroundColor: '#0D1116', border: '1px solid #21262D' }}>
              <MDBCardBody className="text-center">
                <MDBCardImage
                  src="https://static.vecteezy.com/system/resources/previews/009/292/244/original/default-avatar-icon-of-social-media-user-vector.jpg"
                  alt="avatar"
                  className="rounded-circle"
                  style={{ width: '150px', height: '150px' }}
                  fluid 
                />
                <br />
                <p className="text-light mb-1 mt-4 w-3 fw-bold">{userName}</p>
                <p className="text-muted mb-1 w-3 fw-bold">{userId}</p>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="8">
            {/* Card with imoprtant selected info */}
            <MDBCard className="mb-4" style={{ backgroundColor: '#0D1116', border: '1px solid #21262D' }}>
              <MDBCardHeader alignment='center' className="text-light border-0 justify-content-center" style={{ backgroundColor: '#161B22'}}>
                <MDBDropdown>
                  <MDBDropdownToggle color='dark' className='p-1 me-3 shadow-0' style={{ backgroundColor: '#161B22'}}>Информация о пользователе</MDBDropdownToggle>
                  <MDBDropdownMenu style={{ backgroundColor: '#161B22'}}>
                    { 
                      users.map((user : {id: string, name: string}) => 
                      {
                       return <MDBDropdownItem 
                       link id={user.id} onClick={() => handleGetAnotherUserInfo(user.id as string)}>
                       {user.name}
                       </MDBDropdownItem>
                      }) 
                    }
                  </MDBDropdownMenu>
                </MDBDropdown>
              </MDBCardHeader>
              <MDBCardBody>
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText className="text-light">Full Name</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-light" onCopy={(e : any) => handleCopyInfo(e)}>{userInfo?.full_name}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText className="text-light">Email</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-light" onCopy={(e : any) => handleCopyInfo(e)}>{userInfo?.email}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText className="text-light">Phone</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-light" onCopy={(e : any) => handleCopyInfo(e)} >{userInfo?.phone}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText className="text-light">Mobile</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-light" onCopy={(e : any) => handleCopyInfo(e)} >{userInfo?.mobile}</MDBCardText>
                  </MDBCol>
                </MDBRow>
                <hr />
                <MDBRow>
                  <MDBCol sm="3">
                    <MDBCardText className="text-light">Address</MDBCardText>
                  </MDBCol>
                  <MDBCol sm="9">
                    <MDBCardText className="text-light" onCopy={(e : any) => handleCopyInfo(e)} >{userInfo?.address}</MDBCardText>
                  </MDBCol>
                </MDBRow>
              </MDBCardBody>
            </MDBCard>

            {/* Card for choosing object in list for viewving info */}
            <MDBRow>
              <MDBCol>
                <MDBCard className="mb-5 mt-3" style={{ backgroundColor: '#0D1116', border: '1px solid #21262D' }}>
                  <MDBCardHeader className="text-light border-0" style={{ backgroundColor: '#161B22'}}>Твои привилегии</MDBCardHeader>
                  <MDBCardBody>
                    {
                      userPrivInfo?.edit_priv != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Редактирование прав</MDBBtn> 
                    }
                    {
                      userPrivInfo?.edit != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Редактирование</MDBBtn> 
                    }
                    {
                      userPrivInfo?.copy != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Копирование</MDBBtn> 
                    }
                    {
                      userPrivInfo?.view != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Просмотр</MDBBtn> 
                    }  
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}

export default App;
