import {useState, useEffect} from 'react';
import './App.css';
import Modal from './components/Modal';
import Navbar from './components/Navbar';
import Profile from './components/Profile';
import UserInfo from './components/UserInfo';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBCardHeader,
  MDBBtn,
  MDBModalFooter,
  MDBDropdown,
  MDBDropdownItem,
  MDBDropdownMenu,
  MDBDropdownToggle,
  MDBNavbar,
  MDBInput
} from 'mdb-react-ui-kit';

function App() {
  {/* 
    HGETALL user:1
    1) "id"
    2) "@admin"
    3) "name"
    4) "Admin"
    5) "role"
    6) "2"

      HGETALL user_info:1
       1) "id"
       2) "@admin"
       3) "full_name"
       4) "Artjom"
       5) "email"
       6) "sda20036@gmail.com"
       7) "phone"
       8) "89171846394"
       9) "mobile"
      10) "-"
      11) "address"
      12) "Tatischeva"

    get num_users
    "3"

    HGETALL role_priv:1
     1) "role"
     2) "2"
     3) "edit_priv"
     4) "1"
     5) "edit"
     6) "1"
     7) "copy"
     8) "1"
     9) "view"
    10) "1"
   */}
  const [roleSettingsModal, setRoleSettingsModal] = useState(false);
  const [users, setUsers] = useState([]);
  const [userRole, setUserRole] = useState(2);
  const [userId, setUserId] = useState<string>("@admin");
  const [userName, setUserName] = useState("Admin");
  const [userInfo, setUserInfo] = useState<Info>();
  const [userToView, setUserToView] = useState<string>("@admin");
  const [userPrivInfo, setUserPrivInfo] = useState<Privs>();
  const [editMode, setEditMode] = useState(false);

  const toggleOpenSettings = () => setRoleSettingsModal(!roleSettingsModal);
  const toggleEditMode = () => setEditMode(!editMode);
  const handleGetAnotherUserInfo = (id: string) => {
    if(userPrivInfo?.view != "0")
    {
      // Retrieving another user info
      setUserToView(id);
      let count = 1;
      users.map((user : {id: string, name: string}) => {
        if(user.id == id) {
          fetch(`http://localhost:5000/get_user_info?id=${count}`).then(
            res => res.json()
          ).then(
            data => {
              setUserInfo(data)
              console.log(data)
            }
          );
        }
        count++;
      })
      console.log("\nyou are viewing ", id);
    }
    else {
      alert("Not enough privilliges!");
    }
  }

  const handleCopyInfo = (e : any) => {
    if(userPrivInfo?.copy == "0")
    {
      e.preventDefault();
      alert("Not enough privilliges!")
      return false;      
    }
  }

  const handleSwitchRole = (id : string) => {
    setUserId(id);
    let count = 1;
    users.map((user : {id: string, name: string, role: number}) => {
      if(user.id == id) {
        setUserRole(user.role);
        setUserName(user.name);
        console.log("found user", user)

        // Get user info
        fetch(`http://localhost:5000/get_user_info?id=${count}`).then(
          res => res.json()
        ).then(
          data => {
            setUserInfo(data)
            console.log(data)
          }
        )
        // Get user privilleges
        fetch(`http://localhost:5000/get_role_priv?role=${user.role}`).then(
          res => res.json()
        ).then(
          data => {
            setUserPrivInfo(data)
            console.log(data)
          }
        )
      }
      count++;
    })
    console.log("\nyou are now ", id);
  }

  useEffect(() => {
    fetch("http://localhost:5000/get_users").then(
      res => res.json()
    ).then(
      data => {
        setUsers(data)
        console.log(data)
      }
    );
  }, [])

  return (
    <section style={{ backgroundColor: '#0D1116', color: 'white', height: "100%" }}>

      {/* Modal with settings */}
      <Modal roleSettingsModal={roleSettingsModal} setRoleSettingsModal={setRoleSettingsModal}/>

      {/* Navigation bar */}
      <Navbar users={users} handleSwitchRole={handleSwitchRole} roleSettingsModal={roleSettingsModal} setRoleSettingsModal={setRoleSettingsModal}/>

      {/* Main content */}
      <MDBContainer className="py-5 mt-4">
        <MDBRow>
          <MDBCol lg="4">
            {/* Card for profile avatar and actions */}
            <Profile userName={userName} userId={userId}/>
          </MDBCol>

          <MDBCol lg="8">
            {/* Card with important selected info */}
            <UserInfo users={users} handleGetAnotherUserInfo={handleGetAnotherUserInfo} editMode={editMode} userInfo={userInfo} handleCopyInfo={handleCopyInfo} toggleEditMode={toggleEditMode}/>

            {/* Card for choosing object in list for viewing info */}
            <MDBRow>
              <MDBCol>
                <MDBCard className="mb-5 mt-2" style={{ backgroundColor: '#0D1116', border: '1px solid #21262D' }}>
                  <MDBCardHeader className="text-light border-0" style={{ backgroundColor: '#161B22'}}>Твои привилегии</MDBCardHeader>
                  <MDBCardBody className='p-3'>
                    {
                      userPrivInfo?.edit_priv != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Редактирование прав</MDBBtn> 
                    }
                    {
                      userPrivInfo?.edit != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Редактирование</MDBBtn> 
                    }
                    {
                      userPrivInfo?.copy != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Копирование</MDBBtn> 
                    }
                    {
                      userPrivInfo?.view != "0" 
                      &&
                      <MDBBtn color='success' disabled className='m-2'>Просмотр</MDBBtn> 
                    }  
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            </MDBRow>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}

export default App;
